<?php// Tao Title section$wp_customize->add_section(    'title_header',    array(        'title'    => __( 'Blog', 'autos' ),        'priority' => 30,    ));// Tao blog_title.$wp_customize->add_setting(    'blog_title',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'title_header',    ));// Tao blog_title control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'blog_title',        array(            'label'    => __( 'Blog Title', 'autos' ),            'section'  => 'title_header',            'settings' => 'blog_title',            'type'     => 'text',        )    ));// Tao color setting.$wp_customize->add_setting(    'blog_color',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'title_header',        'default'    => '#ffffff',    ));// Tao color control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'blog_color',        array(            'label'    => __( 'Color blog', 'autos' ),            'section'  => 'title_header',            'settings' => 'blog_color',        )    ));//-------------------- Tao section Title meta// Tao meta_title.$wp_customize->add_setting(    'meta_title',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'title_header_meta',        'default'    => __( 'Description Blog', '' ),    ));// Tao meta_title control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'meta_title',        array(            'label'    => __( 'Blog Title', 'autos' ),            'section'  => 'title_header_meta',            'settings' => 'meta_title',            'type'     => 'text',        )    ));