ting(    'footer_right',    array(        'type'              => 'option', // 'option' || 'theme_mod        'capability'        => 'edit_theme_options',        'section'           => 'footer',    ));// Tao FAQ control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'footer_right',        array(            'label'    => __( 'Footer right', 'autos' ),            'section'  => 'footer',            'settings' => 'footer_right',            'type'     => 'textarea',        )    ));