<?php//-------------------1 Tao panel Header -------------------------// Tao panel Header$wp_customize->add_panel(    'header',    array(        'title'      => __( 'Header', 'autos' ),        'priority'   => 30,    ));//--------------------- Tao section ---------------------------------// Tao topbar section.$wp_customize->add_section(    'topbar',    array(        'title'    => __( 'Topbar', 'autos' ),        'panel'    => 'header',    ));// Tao section Header Contene.$wp_customize->add_section(    'header_content',    array(        'title'    => __( 'Header Content', 'autos' ),        'panel'    => 'header',    ));// Tao section Page Header hotline.$wp_customize->add_section(    'page_header',    array(        'title'    => __( 'Page header', 'autos' ),        'panel'    => 'header',    ));//----------------------------------------Setting----------------------------------//--------------------------------------- Page Header Hotline // Tao hotline.$wp_customize->add_setting(    'hotline',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Tao hotline control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'hotline',        array(            'label'    => __( 'Header Content', 'autos' ),            'section'  => 'header_content',            'settings' => 'hotline',            'type'     => 'textarea',        )    ));// Tao background_header_image color.$wp_customize->add_setting(    'background_header_image',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Tao background color control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'background_header_image',        array(            'label'    => __( 'Background Color', 'autos' ),            'section'  => 'page_header',            'settings' => 'background_header_image',        )    ));//---------------------------------------- Background Image// Tao Background Image header section$wp_customize->add_setting(    'page_header_background',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Tao Background Image header control.$wp_customize->add_control(    new WP_Customize_Image_Control(        $wp_customize,        'page_header_background',        array(            'label'    => __( 'Background', 'autos' ),            'section'  => 'page_header',            'settings' => 'page_header_background',        )    ));//---------------------------------------- Topbar// Tao topbar left setting.$wp_customize->add_setting(    'topbar_left',    array(        'type'              => 'option', // 'option' || 'theme_mod        'capability'        => 'edit_theme_options',        'section'           => 'topbar',    ));// Tao topbar left control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'topbar_left',        array(            'label'    => __( 'Header Left', 'autos' ),            'section'  => 'topbar',            'settings' => 'topbar_left',            'type'     => 'textarea',        )    ));// Tao topbar right setting.$wp_customize->add_setting(    'topbar_right',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Tao topbar right control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'topbar_right',        array(            'label'    => __( 'Header Right', 'autos' ),            'section'  => 'topbar',            'settings' => 'topbar_right',            'type'     => 'textarea',        )    ));