<?php// Creating the widget class Flickr_Gallery_Widget extends WP_Widget {         function __construct() {        parent::__construct(            'my_first_widget',            __( 'First Widget', 'autos' ),             array(                'description' => __( 'Sample widget based on First Widget', 'autos' ),            )         );    }                 // Widget Backend     public function form( $instance ) {        $title     = isset( $instance['title'] ) ? $instance['title'] : __( 'My First Widget', 'autos' );        $flickr_id = isset( $instance['flickr_id'] ) ? $instance['flickr_id'] : __( 'Hello', 'autos' );        // Widget admin form        ?>        <p>            <label for="<?php echo $this->get_field_id( 'title' ); ?>"><?php _e( 'Title:' ); ?></label>             <input class="widefat" id="<?php echo $this->get_field_id( 'title' ); ?>" name="<?php echo $this->get_field_name( 'title' ); ?>" type="text" value="<?php echo esc_attr( $title ); ?>" />        </p>        <p>            <label for="<?php echo $this->get_field_id( 'flickr_id' ); ?>"><?php esc_html_e( 'Flickr ID', 'autos' ); ?></label>             <input class="widefat" id="<?php echo $this->get_field_id( 'flickr_id' ); ?>" name="<?php echo $this->get_field_name( 'flickr_id' ); ?>" type="text" value="<?php echo esc_attr( $flickr_id ); ?>" />        </p>        <?php     }             // Updating widget replacing old instances with new    public function update( $new_instance, $old_instance ) {        $instance = array();        $instance['title'] = ( ! empty( $new_instance['title'] ) ) ? strip_tags( $new_instance['title'] ) : '';        $instance['flickr_id'] = ( ! empty( $new_instance['flickr_id'] ) ) ? strip_tags( $new_instance['flickr_id'] ) : '';        return $instance;    }    // Creating widget front-end         public function widget( $args, $instance ) {        $title     = $instance['title'];        $flickr_id = $instance['flickr_id'];                 // before and after widget arguments are defined by themes        echo $args['before_widget'];        echo esc_html_e( 'Title', 'autos' );        echo esc_html_e( 'Flickr ID', 'autos' );        echo $args['after_widget'];    }}